mapper.update:
UPDATE faq
SET faq_subject=#{faq_subject}, faq_content=#{faq_content},	faq_category=#{faq_category}
WHERE faq_idx=#{faq_idx}

mapper.select:
SELECT COUNT(*)
FROM qna

	WHERE qna_status = #{answer_status}

SELECT *
FROM qna

	WHERE qna_status = #{answer_status}

ORDER BY qna_status ASC, qna_idx  DESC 
LIMIT #{startRow}, #{listLimit}

mapper.insert:
UPDATE qna
SET admin_content = #{admin_content}, qna_status = 1
WHERE qna_idx = #{qna_idx}

mapper.select:
SELECT COUNT(member_id)
FROM member

	WHERE member_license_checked = #{license_auth}

SELECT CONCAT(SUBSTR(member_id, 1, 4),"***") AS member_id, member_name, member_email, member_license_checked, CASE WHEN member_license_checked = 1 THEN '인증 완료' WHEN member_license_checked = 2 THEN '미인증' END license_checked, -- 인증 완료 여부 CASE WHEN member_license_checked = 1 THEN 'black' WHEN member_license_checked = 2 THEN 'red' END color, -- 인증 완료 여부에 따른 색상 CASE WHEN TIMESTAMPDIFF(YEAR, STR_TO_DATE(member_birth, '%Y%m%d'), CURDATE()) IS NULL THEN '나이 인식 불가' WHEN TIMESTAMPDIFF(YEAR, STR_TO_DATE(member_birth, '%Y%m%d'), CURDATE()) >= 23 THEN '만 23세 이상' ELSE '만 23세 미만' END age_group -- 나이 FROM member
WHERE member_license_checked = #{license_auth}
ORDER BY member_idx DESC LIMIT #{listLimit}, #{startRow}
