mapper.insert:

	SELECT IFNULL(MAX(board_idx), 0)
	FROM notice

INSERT
INTO notice
VALUES (
	#{board_idx} + 1
	, #{board_subject}
	, #{board_content}
	, #{board_writer}
	, now()
	, #{board_important}
)

mapper.update:
UPDATE notice
SET board_subject=#{board_subject}, board_content=#{board_content}, board_important=#{board_important} 
WHERE board_idx=#{board_idx}

mapper.select:
SELECT *
FROM faq

	WHERE faq_category = #{category}

ORDER BY faq_idx DESC

LIMIT  #{listLimit}, #{startRow}

SELECT COUNT(*)
FROM faq
WHERE faq_category = #{category}

SELECT *
FROM qna
WHERE qna_writer = #{id}
ORDER BY qna_idx DESC
LIMIT #{startRow}, #{listLimit}

SELECT COUNT(*)
FROM qna
WHERE qna_writer =#{writer}

