mapper.insert:

	SELECT IFNULL(MAX(board_idx), 0)
	FROM notice

INSERT
INTO notice
VALUES (
	#{board_idx} + 1
	, #{board_subject}
	, #{board_content}
	, #{board_writer}
	, #{board_readcount}
	, now()
)

mapper.update:
UPDATE notice
SET board_subject=#{board_subject}, board_content=#{board_content} 
WHERE board_idx=#{board_idx}
